// import { useInView } from 'framer-motion';
import React from 'react';

import CountUp from 'react-countup';
import {useInView} from 'react-intersection-observer'
import { motion } from 'framer-motion';

import { fadeIn } from '../variants'
const About = () => {
const [ref, inView] = useInView({
  threshold: 0.5,
})

  return <section id='about' className='section' ref={ref}>

    <div className="container mx-auto">
      <div>
        <div className='flex-1 bg-about bg-contain bg-no-repeat h-[640px] mix-blend-lighten bg-top'>
          
        </div>
        <div>
          <h2>About me</h2> 
          <h3>I'am a frelance Front-end Developer whith 1 year of experience.</h3>
          <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Dicta perspiciatis rem sequi?</p>
          <div>
            <div>
              <div className='text-[40px]'>
                <CountUp start={0} end={13} duration={3}/>
              </div>
              <div className='font-primary text-sm tracking-[2px]'>
                Years of <br /> 
                experience
              </div>
            </div>
          </div>
        </div>
        
      </div>
    </div>

  </section>;
};

export default About;
