import React from 'react';

import { BsArrowUpRight } from 'react-icons/bs';

import { motion } from 'framer-motion';

import { fadeIn } from '../variants'
// import Micarta from './Micarta';
import Css from './Css';


const Services = () => {
  return <div className='container mx-auto'>
    <div id='services' className='section'>
      <motion.div variants={fadeIn('left', 0.5)} initial="hidden" whileInView={'show'} viewport={{ once: false, amount: 0.3 }} className='flex-1 mx-10'>
        <h2 className='h2 text-accent'>My skills</h2>
        <h3 className='h3 mb-4'>I'am a frelance Front-end Developer whith 1 year of experience.</h3>
        <p className='mb-6'>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Dicta perspiciatis rem sequi?</p>
        
        <div className='flex gap-x-8 items-center'>
          <button className='btn btn-lg'>Contact me</button>
          <a href="#" className='text-gradient btn-link'>my portfolio</a>
        </div>
      </motion.div>
      
      
    </div>
    <Css/>
  </div>


};

export default Services;
