import React from 'react';

import Image from '../assets/avatar.svg';

import { FaGithub, FaYoutube, FaDribbble } from 'react-icons/fa';

import { TypeAnimation } from 'react-type-animation';

import { motion } from 'framer-motion';

import { fadeIn } from '../variants'
const Banner = () => {
  return <section id='home' className='section'>
    <div className="container mx-auto">
      <div>
        <div>
          <h1>Daniel <span>Ferrer</span></h1>
          <div className='mb-6 text-[36px] lg:text-[60px] font-secondary font-semibold uppercase leading-[1]'>
            <span className='text-white mr-4'>I am a</span>
            <TypeAnimation sequence={[
              ' Frontend developer',
              2000,
              'UI UX Developer',
              2000,
              'React Developer',
              2000,
            ]}
              speed={50}
              className='text-accent'
              wrapper='span'
              repeat={Infinity}
            />
            
          </div>
          <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ratione quis odio earum voluptatibus incidunt nulla nobis laborum perferendis explicabo maiores.</p>
          <div>
            <button className='btn btn-lg'>
              Contact me
            </button>
            <a href="#">My portfolio</a>
          </div>
        </div>
        <div>
          <img src={Image} alt="mifoto" />
        </div>
      </div>
    </div>
  </section>;
};

export default Banner;
